package Session2;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// data type integer
		int day = 10;
		int month = 4;
		int year = 1980;
					
		System.out.println("My date of Birth = "+day+"-"+month+"-"+year);
		// data type double
		double price = 50.09;
		System.out.println("Price of Item : "+price);	
		
		// data type String
		
		String firstName = "Mark";
		String lastName = "Taylor";
		System.out.println("My first Name is "+firstName+" and last name is "+lastName);
		
		// data type Boolean
		boolean choice = true;
		System.out.println("Choice = "+choice);
		
	}

}
